create table IF NOT EXISTS book (id bigint not null auto_increment, active bit not null, author varchar(255), category varchar(255), description text, format varchar(255), in_stock_number integer not null, isbn integer not null, language varchar(255), list_price double precision not null, number_of_pages integer not null, our_price double precision not null, publication_date varchar(255), publisher varchar(255), shipping_weight double precision not null, title varchar(255), primary key (id)) engine=InnoDB;


